import React, { Component } from 'react';
import './Home.css';
import axios from 'axios'
import 'react-toastify/dist/ReactToastify.css';
import { Container, Row, Col} from 'reactstrap';
import { Card, CardBody, CardTitle, CardText, CardImg } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export class PostAndComments extends Component {
    static displayName = PostAndComments.name;
    constructor(props) {
        super(props);
        this.state = {
            item: null,
            postID: localStorage.getItem('id'),
            Text: ''
        }
    }

    componentDidMount() {
        axios.get('https://localhost:44399/posts/' + this.state.postID)
        .then(
          (result) => {
            this.setState({
              item: result.data
            })
          }
          );
        }
    
    handleChange = (event) => {
        this.setState({[event.target.name]: event.target.value});
    }

    handleSubmit = () => {
        const fd = new FormData();
        const token = localStorage.getItem('token');
        console.log(token);
        const config = {
            headers: { Authorization: `Bearer ${token}` }
        };

        fd.append('Text', this.state.Text);
        axios.post('https://localhost:44399/posts/AddComment/' + this.state.postID, fd,config)
        .then(res => {
            console.log(res);
            if (res.status === 200){
                toast.success('Képfeltöltés sikeres',{
                    position: "bottom-right"});
            }
        })
      }

    render() {
        let data;
        axios.get('https://localhost:44399/posts/' + this.state.postID).then(result => {data = result.data})
        return (
            <Container>
                <Card className="card--post">
                    <CardImg top className="card-img"  src={'https://localhost:44399/posts/images/' + this.state.postID} alt="Card image cap" />
                    <CardBody>
                        <CardTitle tag="h5">{`${data.userName} képe`}</CardTitle>
                        <CardText>
                            {data.description}
                        </CardText>
                        <CardText>
                            {data.postTime}
                        </CardText>
                    </CardBody>
                </Card>
                <Row>
                    {data.comments.map(comment =>
                        <Col xs="12" sm="12" md="6" lg="4" className="image-item">
                            <div>{comment.creator.userName} wrote: {comment.Text} at {comment.postTime}</div>
                        </Col>
                    )}
                </Row>
                <Row>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Write comment:
                        <input type="text" value={this.state.Text} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
                </Row>
                <ToastContainer/>
            </Container>
           
            )
    }
}
