import React, { Component } from 'react';
import {Container, Button, Toast, Input, ToastBody} from 'reactstrap';
import './Upload.css';
import axios from 'axios'

export class Upload extends Component {
    static displayName = Upload.name;
    constructor(props) {
        super(props);
        this.state = {
            show: false,
            selectedFile: null ,
            description: null 
        }
    }

    fileSelectedHandler = event => {
        this.setState({
            selectedFile: event.target.files[0]
        })
    }

    descriptionChanged = event => {
        this.setState({
            description: event.target.value
        })
    }

    header = () =>{
        const h = new Headers
    };

    upload() {
        const fd = new FormData();
        const token = localStorage.getItem('token');
        console.log(token);
        const config = {
            headers: { Authorization: `Bearer ${token}` }
        };

        fd.append('PictureData', this.state.selectedFile)
        fd.append('Description', this.state.description)
        axios.post('https://localhost:44399/posts/', fd,config)
        .then(res => {
            console.log(res);
            if (res == 200){
                this.setState({
                    show: true
                })
            }
        })
    }

    render() {
        const state = this.state;
        return (
            <Container>
                <Toast isOpen={state.show}>
                    <ToastBody>Képfeltöltés sikeres</ToastBody>
                </Toast>
                <div className="upload-wrapper">
                    <h1 className="text-center page-header">Képfeltöltés</h1>
                    <div className="upload">
                        <h3 className="upload__title">Válassz képet</h3>
                        <Input type="file" onChange={this.fileSelectedHandler}/>
                    </div>
                    <div className="description">
                        <h3 className="upload__title desc_label" >Leírás</h3>
                        <Input type="textarea" onChange={this.descriptionChanged}/>
                    </div>
                    <Button className="button-upload" color="primary" onClick={() => {this.upload()}}>Feltöltés</Button>
                </div>
            </Container>
        )
    }
}
