import React, { Component } from 'react';
import './Home.css';
import {Container, Row, Col} from 'reactstrap';
import { Card, CardBody, CardTitle, CardText, CardImg } from 'reactstrap';
import axios from 'axios';

export class Home extends Component {
  static displayName = Home.name;

  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
      items: [],
      error: null,
      imageIsLoaded: false
    }
  }

  componentDidMount() {
    axios.get('https://localhost:44399/posts/')
    .then(
      (result) => {
        console.log(result)
        this.setState({
          isLoaded: true,
          items: result
        })
      },
      (error) => {
        this.setState({
          isLoaded: true,
          error
        })
      });
    }

  render () {
    const { items } = this.state;
    console.log(items)
    return (
      <Container>
        <h1 className="text-center page-header">Folyam</h1>
        <Row>
          {items.map(item =>
            <Col xs="12" sm="12" md="6" lg="4" className="image-item" key={item.id}>
              <Card className="card--post">
                <CardImg top width="100%" src={item.download_url} alt="Card image cap" />
                <CardBody>
                  <CardTitle tag="h5">{`${item.author} képe`}</CardTitle>
                  <CardText>
                    {item.description}
                  </CardText>
                </CardBody>
              </Card>
            </Col>
          )}
        </Row>
      </Container>
    );
  }
}
