import React, { Component } from 'react';
import {Container, Button, Toast, Input, ToastBody} from 'reactstrap';
import './Upload.css';
import axios from 'axios'

export class Upload extends Component {
    state = {
        selectedFile: null  
    }
    static displayName = Upload.name;
    constructor(props) {
        super(props);
        this.state = {
            show: false
        }
    }

    fileSelectedHandler = event => {
        this.setState({
            selectedFile: event.target.files[0]
        })
    }

    upload() {
        const fd = new FormData();

        fd.append('PictureData', this.state.selectedFile, this.state.selectedFile.name)
        fd.append('Description', 'Some text')
        axios.post('https://localhost:44399/posts/', fd)
        .then(res => {
            console.log(res);
            if (res == 200){
                this.setState({
                    show: true
                })
            }
        })
    }

    render() {
        const state = this.state;
        return (
            <Container>
                <Toast isOpen={state.show}>
                    <ToastBody>Képfeltöltés sikeres</ToastBody>
                </Toast>
                <div className="upload-wrapper">
                    <h1 className="text-center page-header">Képfeltöltés</h1>
                    <div className="upload">
                        <Input type="file" onChange={this.fileSelectedHandler}/>
                        <h3 className="upload__title">Válassz képet</h3>
                    </div>
                    <Button className="button-upload" color="primary" onClick={() => {this.upload()}}>Feltöltés</Button>
                </div>
            </Container>
        )
    }
}
