import React, { Component } from 'react';
import './Home.css';
import axios from 'axios'
import 'react-toastify/dist/ReactToastify.css';
import { Container, Row, Col} from 'reactstrap';
import { Card, CardBody, CardTitle, CardText, CardImg } from 'reactstrap';

export class PostAndComments extends Component {
    static displayName = PostAndComments.name;
    constructor(props) {
        super(props);
        this.state = {
            item: null,
            Text: ''
        }
    }

    componentDidMount() {
        axios.get('https://localhost:44399/posts/' + localStorage.getItem('id'))
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              item: result.data
            })
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            })
          });
        }
    
    handleChange = (event) => {
        this.setState({[event.target.name]: event.target.value});
    }

    handleSubmit(event) {
        alert('A comment was submitted: ' + this.Text);

        fetch('https://localhost:44399/posts/AddComment/' + this.item.postID, {
        method: 'POST',
        body: JSON.stringify(this.state)
      }).then(function(response) {
        console.log(response)
        return response.json();
      });

    event.preventDefault();
    }

    render() {
        return (
            <Container>
                <Card className="card--post">
                    <CardImg top className="card-img"  src={'https://localhost:44399/posts/images/' + this.item.postID} alt="Card image cap" />
                    <CardBody>
                        <CardTitle tag="h5">{`${this.item.userName} képe`}</CardTitle>
                        <CardText>
                            {this.item.description}
                        </CardText>
                        <CardText>
                            {this.item.postTime}
                        </CardText>
                    </CardBody>
                </Card>
                <Row>
                    {this.item.comments.map(comment =>
                        <Col xs="12" sm="12" md="6" lg="4" className="image-item">
                            <div>{comment.creator.userName} wrote: {comment.Text} at {comment.postTime}</div>
                        </Col>
                    )}
                </Row>
                <Row>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Write comment:
                        <input type="text" value={this.Text} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
                </Row>
            </Container>
            )
    }
}
